CREATE PROCEDURE [dbo].[SP_Emprunt_Insert]
    @JeuId UNIQUEIDENTIFIER,
    @PreteurId UNIQUEIDENTIFIER,
    @EmprunteurId UNIQUEIDENTIFIER,
    @EvaluationPreteur INT = NULL,
    @EvaluationEmprunteur INT = NULL
AS

BEGIN
    SET NOCOUNT ON;

    SELECT JeuId, Nom, Description, AgeMin, AgeMax, NbJoueurMin, NbJoueurMax, DureeMinute, DateCreation, UserId
    FROM Jeux;
END;

BEGIN
    SET NOCOUNT ON;

    SELECT JeuId, Nom, Description, AgeMin, AgeMax, NbJoueurMin, NbJoueurMax, DureeMinute, DateCreation, UserId
    FROM Jeux
    WHERE JeuId = @JeuId;
END;

CREATE PROCEDURE SP_Jeux_GetById
    @JeuId UNIQUEIDENTIFIER
AS

BEGIN
    INSERT INTO [Jeux] 
    (
        [Nom],
        [Description],
        [AgeMin],
        [AgeMax],
        [NbJoueurMin],
        [NbJoueurMax],
        [DureeMinute],
        [UserId]
    )
    VALUES 
    (
        @Nom,
        @Description,
        @AgeMin,
        @AgeMax,
        @NbJoueurMin,
        @NbJoueurMax,
        @DureeMinute,
        @UserId
    )
END

