CREATE PROCEDURE [dbo].[SP_User_Insert]
	@email NVARCHAR(320),
 @pseudo Nvarchar(150),
 --@subscription_date dateTime null ,
 @password Nvarchar(150)
AS

BEGIN
    SET NOCOUNT ON;

    UPDATE [dbo].[User]
    SET 
        Pseudo = @Pseudo
    WHERE UserId = @UserId;
END;

